mapper.select:
select ID
from USERS
where ID = #{signUp_email}

select ID, PASSWORD, NAME, ADDRESS, PHONE, USER_LEVEL, MILEAGE
from USERS
where ID = #{email} AND PASSWORD=#{password}

mapper.update:
update USERS
set NAME=#{name}, ADDRESS=#{address}, PHONE=#{phone}
where ID=#{id}

update USERS
set PASSWORD=#{new_pwd}
where ID=#{email}

update USERS
set USER_LEVEL=1
where ID=#{id}
